class Random{static randint(r,n){if("number"==typeof r&&"number"==typeof n)return Math.round(Math.random()*(Math.round(n)-Math.round(r)))+Math.round(r);throw TypeError("randint() arguments must be numbers")}static choice(r){return r[Math.floor(Math.random()*r.length)]}static shuffle(r){if(!Array.isArray(r))throw TypeError("shuffle() arguments must be arrays");for(let n=r.length-1;n>0;n--){let t=Math.floor(Math.random()*(n+1));[r[n],r[t]]=[r[t],r[n]]}return r}}window.random=Random;